 color:#424242;
 margin:0;
 padding:10px 20px 25px 20px;
}

p.frage:before{
 content: "\279E\0020";
}

.zusatz{
 color: #5F5F5F;
 font-size:13px;
 line-height:17px;
 display:block;
 font-style:italic;
 text-align:left;
 margin: 0;
 padding: 15px 20px 0 20px;
}

.zusatz span.anker{
 font-weight:bold;
 font-style:normal;
}

.antwort{
 font-size:18px;
 line-height:24px;
 margin:0;
 padding:20px 20px 0 20px;
}

.extra{
 color:#5D5F5F;
 font-size: 17px;
 margin:0;
 padding: 15px 20px 0 20px;
 font-style:italic;
}

.bild{
 margin:0;
 font-size:17px;
 padding: 25px 20px 0 20px;
}

.bild img, img {
 margin:0;
 max-height: 50%;
}

#tags{
 font-size:13px;
 color:#CBCBCB;
 text-align: left;
 margin: 10px 0;
}

